import React, { useState, useRef, useEffect, useCallback } from 'react';

const API_BASE = 'http://127.0.0.1:8000';

const TimeCopApp = () => {
  const [userId, setUserId] = useState('user_001');
  const [activeTab, setActiveTab] = useState('analyze');
  const [userInput, setUserInput] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [voiceResult, setVoiceResult] = useState(null);
  const [memoryData, setMemoryData] = useState(null);
  const [error, setError] = useState('');
  
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Initialize media recorder
  useEffect(() => {
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices.getUserMedia({ audio: true })
        .then(stream => {
          const mediaRecorder = new MediaRecorder(stream);
          mediaRecorderRef.current = mediaRecorder;
          
          mediaRecorder.ondataavailable = (event) => {
            if (event.data.size > 0) {
              audioChunksRef.current.push(event.data);
            }
          };
          
          mediaRecorder.onstop = async () => {
            const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
            audioChunksRef.current = [];
            await processVoiceInput(audioBlob);
          };
        })
        .catch(err => {
          console.error('Error accessing microphone:', err);
          setError('Microphone access denied. Voice logging unavailable.');
        });
    }
  }, []);

  const startRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'inactive') {
      audioChunksRef.current = [];
      mediaRecorderRef.current.start();
      setIsRecording(true);
      setError('');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const processVoiceInput = async (audioBlob) => {
    setIsLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', audioBlob, 'recording.wav');
      formData.append('user_id', userId);

      const response = await fetch(`${API_BASE}/voice-log`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      
      const result = await response.json();
      setVoiceResult(result);
      setError('');
    } catch (err) {
      setError(`Voice processing failed: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const analyzeProductivity = async () => {
    setIsLoading(true);
    try {
      const formData = new FormData();
      formData.append('user_input', userInput || 'How was my productivity this week?');
      formData.append('user_id', userId);

      const response = await fetch(`${API_BASE}/analyze`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      
      const result = await response.json();
      console.log("Full API result:", result);
      console.log("Coaching data:", result.coaching);
      console.log("Coaching type:", typeof result.coaching);
      setAnalysisResult(result);
      setError('');
    } catch (err) {
      setError(`Analysis failed: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const queryMemory = async (query = null) => {
    setIsLoading(true);
    try {
      const url = new URL(`${API_BASE}/memory/${userId}`);
      if (query) url.searchParams.append('query', query);
      url.searchParams.append('limit', '10');

      const response = await fetch(url);
      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      
      const result = await response.json();
      setMemoryData(result);
      setError('');
    } catch (err) {
      setError(`Memory query failed: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const renderCoachingSection = (coaching) => {
    console.log("renderCoachingSection called with:", coaching);
    console.log("Type of coaching:", typeof coaching);
    
    // Debug: Show raw coaching data
    if (!coaching) {
      return (
        <div>
          <p className="italic text-gray-500">Your personal coach will provide feedback here</p>
          <div className="mt-2 p-2 bg-yellow-100 rounded text-xs">
            <strong>Debug:</strong> coaching is null/undefined
          </div>
        </div>
      );
    }

    let parsedCoaching = coaching;
    
    // Handle string inputs
    if (typeof coaching === 'string') {
      console.log("Coaching is a string:", coaching.substring(0, 100) + "...");
      
      // Handle markdown code blocks
      if (coaching.includes('```json')) {
        const jsonStart = coaching.indexOf('```json') + 7;
        const jsonEnd = coaching.lastIndexOf('```');
        if (jsonEnd > jsonStart) {
          const jsonContent = coaching.substring(jsonStart, jsonEnd).trim();
          try {
            parsedCoaching = JSON.parse(jsonContent);
            console.log("Successfully parsed JSON from markdown:", parsedCoaching);
          } catch (e) {
            console.log("Failed to parse JSON from markdown:", e);
            return (
              <div>
                <p className="whitespace-pre-wrap">{coaching}</p>
                <div className="mt-2 p-2 bg-red-100 rounded text-xs">
                  <strong>Debug:</strong> Failed to parse JSON from markdown: {e.message}
                </div>
              </div>
            );
          }
        }
      } else if (coaching.trim().startsWith('{')) {
        try {
          parsedCoaching = JSON.parse(coaching);
          console.log("Successfully parsed JSON string:", parsedCoaching);
        } catch (e) {
          console.log("Failed to parse JSON string:", e);
          return (
            <div>
              <p className="whitespace-pre-wrap">{coaching}</p>
              <div className="mt-2 p-2 bg-red-100 rounded text-xs">
                <strong>Debug:</strong> Failed to parse JSON string: {e.message}
              </div>
            </div>
          );
        }
      } else {
        console.log("Coaching is plain text, displaying as-is");
        return (
          <div>
            <p className="whitespace-pre-wrap">{coaching}</p>
            <div className="mt-2 p-2 bg-blue-100 rounded text-xs">
              <strong>Debug:</strong> Displaying as plain text
            </div>
          </div>
        );
      }
    }

    // Handle object inputs
    if (parsedCoaching && typeof parsedCoaching === 'object') {
      console.log("Rendering structured coaching:", Object.keys(parsedCoaching));
      
      return (
        <div className="space-y-4">
          {/* Debug info */}
          <div className="p-2 bg-green-100 rounded text-xs">
            <strong>Debug:</strong> Found structured coaching with keys: {Object.keys(parsedCoaching).join(', ')}
          </div>

          {/* Render all properties dynamically */}
          {Object.entries(parsedCoaching).map(([key, value]) => {
            if (!value) return null;
            
            const colors = {
              main_message: { bg: 'bg-purple-100', text: 'text-purple-800', title: 'text-purple-900', icon: '📋', label: 'Summary' },
              specific_tips: { bg: 'bg-blue-50', text: 'text-blue-800', title: 'text-blue-900', icon: '💡', label: 'Specific Tips' },
              time_management: { bg: 'bg-green-50', text: 'text-green-800', title: 'text-green-900', icon: '⏰', label: 'Time Management' },
              focus_strategies: { bg: 'bg-yellow-50', text: 'text-yellow-800', title: 'text-yellow-900', icon: '🎯', label: 'Focus Strategies' },
              motivation: { bg: 'bg-pink-50', text: 'text-pink-800', title: 'text-pink-900', icon: '🚀', label: 'Motivation' },
              next_week_goals: { bg: 'bg-indigo-50', text: 'text-indigo-800', title: 'text-indigo-900', icon: '🎯', label: 'Next Week Goals' }
            };
            
            const style = colors[key] || { bg: 'bg-gray-50', text: 'text-gray-800', title: 'text-gray-900', icon: '📝', label: key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()) };
            
            return (
              <div key={key} className={`${style.bg} p-4 rounded-lg`}>
                <h5 className={`font-semibold ${style.title} mb-2 flex items-center gap-2`}>
                  <span>{style.icon}</span>
                  {style.label}
                </h5>
                <div className={style.text}>
                  {Array.isArray(value) ? (
                    <ul className="space-y-2">
                      {value.map((item, index) => (
                        <li key={index} className="flex items-start gap-2">
                          <span className="mt-1">•</span>
                          <span dangerouslySetInnerHTML={{ __html: typeof item === 'string' ? item.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') : String(item) }} />
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p dangerouslySetInnerHTML={{ __html: typeof value === 'string' ? value.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') : String(value) }} />
                  )}
                </div>
              </div>
            );
          })}
        </div>
      );
    }

    // Fallback for any other case
    return (
      <div>
        <p className="whitespace-pre-wrap">{JSON.stringify(coaching, null, 2)}</p>
        <div className="mt-2 p-2 bg-gray-100 rounded text-xs">
          <strong>Debug:</strong> Fallback JSON display
        </div>
      </div>
    );
  };

  const handleInputChange = useCallback((e) => {
    setUserInput(e.target.value);
  }, []);

  const handleKeyPress = useCallback((e) => {
    if (e.key === 'Enter') {
      analyzeProductivity();
    }
  }, []);

  const handleMemoryKeyPress = useCallback((e) => {
    if (e.key === 'Enter') {
      queryMemory(e.target.value);
    }
  }, []);

  const handleUserIdChange = useCallback((e) => {
    setUserId(e.target.value);
  }, []);

  const TabButton = ({ id, icon, label, isActive, onClick }) => (
    <button
      onClick={() => onClick(id)}
      className={`flex items-center gap-3 px-6 py-3 rounded-lg transition-all duration-200 ${
        isActive 
          ? 'bg-blue-600 text-white shadow-lg transform scale-105' 
          : 'bg-gray-100 text-gray-700 hover:bg-gray-200 hover:scale-102'
      }`}
    >
      <span className="text-xl">{icon}</span>
      <span className="font-medium">{label}</span>
    </button>
  );

  const DataCard = ({ title, children, icon, color = "blue" }) => (
    <div className={`bg-white rounded-xl shadow-lg border-l-4 border-${color}-500 p-6 mb-6`}>
      <div className="flex items-center gap-3 mb-4">
        <div className={`p-2 bg-${color}-100 rounded-lg`}>
          <span className={`text-2xl text-${color}-600`}>{icon}</span>
        </div>
        <h3 className="text-xl font-bold text-gray-800">{title}</h3>
      </div>
      {children}
    </div>
  );

  // Add test button for debugging
  const addTestData = () => {
    const testResult = {
      coaching: {
        main_message: "You've had a productive week with consistent coding sessions and good meeting attendance.",
        specific_tips: [
          "**Focus blocks**: Try 90-minute deep work sessions",
          "**Email batching**: Check emails only 2-3 times per day",
          "**Break scheduling**: Use the Pomodoro technique"
        ],
        time_management: "Your calendar shows good time blocking, but consider adding buffer time between meetings.",
        focus_strategies: "Based on your GitHub activity, you work best in the morning. Protect this time for complex coding tasks.",
        motivation: "You're making great progress! Your commit frequency has increased 40% this week.",
        next_week_goals: [
          "Complete the authentication module",
          "Reduce meeting time by 20%",
          "Implement automated testing pipeline"
        ]
      },
      status: "success"
    };
    setAnalysisResult(testResult);
  };

  const renderAnalysisTab = () => (
    <div className="space-y-6">
      <DataCard title="Productivity Analysis" icon="📊" color="blue">
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Ask TimeCop about your productivity:
            </label>
            <div className="flex gap-3">
              <input
                key="productivity-input"
                type="text"
                value={userInput}
                onChange={handleInputChange}
                placeholder="How was my week? What should I improve?"
                className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                onKeyPress={handleKeyPress}
                autoComplete="off"
                spellCheck="false"
              />
              <button
                onClick={analyzeProductivity}
                disabled={isLoading}
                className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2 transition-colors"
              >
                <span>📤</span>
                {isLoading ? 'Analyzing...' : 'Analyze'}
              </button>
              <button
                onClick={addTestData}
                className="px-4 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center gap-2 transition-colors"
              >
                <span>🧪</span>
                Test
              </button>
            </div>
          </div>

          {analysisResult && (
            <div className="space-y-4 mt-6">
              {/* Debug: Show full API response structure */}
              <div className="bg-gray-100 p-4 rounded-lg">
                <h4 className="font-semibold text-gray-800 mb-2">🔍 Debug: Full API Response</h4>
                <div className="text-sm">
                  <p><strong>Keys in response:</strong> {Object.keys(analysisResult).join(', ')}</p>
                  <p><strong>Has coaching field:</strong> {'coaching' in analysisResult ? 'Yes' : 'No'}</p>
                  <p><strong>Coaching value:</strong> {analysisResult.coaching === null ? 'null' : analysisResult.coaching === undefined ? 'undefined' : typeof analysisResult.coaching}</p>
                  <div className="mt-2 max-h-32 overflow-y-auto bg-white p-2 rounded border">
                    <pre className="text-xs">{JSON.stringify(analysisResult, null, 2)}</pre>
                  </div>
                </div>
              </div>

              <div className="bg-gradient-to-r from-purple-50 to-pink-50 p-6 rounded-lg">
                <h4 className="font-semibold text-purple-800 mb-4 flex items-center gap-2">
                  <span>💬</span>
                  TimeCop Coaching
                </h4>
                <div className="text-purple-700">
                  {renderCoachingSection(analysisResult.coaching)}
                </div>
              </div>
            </div>
          )}
        </div>
      </DataCard>
    </div>
  );

  const renderVoiceTab = () => (
    <div className="space-y-6">
      <DataCard title="Voice Logging" icon="🎤" color="green">
        <div className="space-y-4">
          <div className="text-center">
            <button
              onClick={isRecording ? stopRecording : startRecording}
              disabled={isLoading}
              className={`px-8 py-4 rounded-full text-white font-semibold flex items-center gap-3 mx-auto transition-all duration-200 ${
                isRecording 
                  ? 'bg-red-500 hover:bg-red-600 animate-pulse' 
                  : 'bg-green-500 hover:bg-green-600'
              } disabled:opacity-50 disabled:cursor-not-allowed`}
            >
              {isRecording ? (
                <>
                  <span>⏹️</span>
                  Stop Recording
                </>
              ) : (
                <>
                  <span>▶️</span>
                  Start Voice Log
                </>
              )}
            </button>
            
            {isRecording && (
              <p className="text-sm text-gray-600 mt-3 animate-bounce">
                🎤 Recording... Speak about your current activity or how you're feeling
              </p>
            )}
          </div>

          {voiceResult && (
            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-semibold text-gray-800 mb-3">Voice Log Result</h4>
              <div className="space-y-3">
                <div>
                  <span className="font-medium text-gray-700">Transcription:</span>
                  <p className="text-gray-600 mt-1">{voiceResult.transcription?.transcription || 'Processing...'}</p>
                </div>
                <div>
                  <span className="font-medium text-gray-700">Activity Detected:</span>
                  <p className="text-gray-600 mt-1">{voiceResult.transcription?.activity || 'None detected'}</p>
                </div>
                <div>
                  <span className="font-medium text-gray-700">Emotional Tone:</span>
                  <p className="text-gray-600 mt-1">{voiceResult.transcription?.emotional_tone || 'Neutral'}</p>
                </div>
                {voiceResult.stored_in_memory && (
                  <div className="text-green-600 text-sm flex items-center gap-2">
                    <span>🧠</span>
                    Stored in long-term memory
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </DataCard>
    </div>
  );

  const renderMemoryTab = () => (
    <div className="space-y-6">
      <DataCard title="Memory & Trends" icon="🧠" color="purple">
        <div className="space-y-4">
          <div className="flex gap-3">
            <input
              key="memory-search-input"
              type="text"
              placeholder="Search your productivity history..."
              className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
              onKeyPress={handleMemoryKeyPress}
              autoComplete="off"
              spellCheck="false"
            />
            <button
              onClick={() => queryMemory()}
              disabled={isLoading}
              className="px-6 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 disabled:opacity-50 flex items-center gap-2"
            >
              <span>🔍</span>
              Query Memory
            </button>
          </div>

          {memoryData && (
            <div className="space-y-4">
              {memoryData.trends && (
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="font-semibold text-purple-800 mb-2 flex items-center gap-2">
                    <span>📈</span>
                    Productivity Trends
                  </h4>
                  <div className="text-sm text-purple-700">
                    {typeof memoryData.trends === 'string' ? (
                      <p>{memoryData.trends}</p>
                    ) : (
                      <p>Long-term trends will appear here</p>
                    )}
                  </div>
                </div>
              )}

              <div className="bg-gray-50 p-4 rounded-lg">
                <h4 className="font-semibold text-gray-800 mb-3">Recent Memory Entries</h4>
                <div className="space-y-2 max-h-64 overflow-y-auto">
                  {memoryData.memory ? (
                    <pre className="text-sm text-gray-600 whitespace-pre-wrap">{memoryData.memory}</pre>
                  ) : (
                    <p className="text-gray-500 italic">No memory entries found</p>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>
      </DataCard>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50">
      {/* Header */}
      <header className="bg-white shadow-lg border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="p-3 bg-gradient-to-r from-blue-600 to-purple-600 rounded-xl">
                <span className="text-white text-3xl">⏰</span>
              </div>
              <div>
                <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                  TimeCop
                </h1>
                <p className="text-gray-600">Multi-Agent Productivity System</p>
              </div>
            </div>
            
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <span className="text-gray-600">👤</span>
                <input
                  key="user-id-input"
                  type="text"
                  value={userId}
                  onChange={handleUserIdChange}
                  className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                  placeholder="User ID"
                  autoComplete="off"
                  spellCheck="false"
                />
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex gap-4 overflow-x-auto">
            <TabButton
              id="analyze"
              icon="📊"
              label="Analyze"
              isActive={activeTab === 'analyze'}
              onClick={setActiveTab}
            />
            <TabButton
              id="voice"
              icon="🎤"
              label="Voice Log"
              isActive={activeTab === 'voice'}
              onClick={setActiveTab}
            />
            <TabButton
              id="memory"
              icon="🧠"
              label="Memory"
              isActive={activeTab === 'memory'}
              onClick={setActiveTab}
            />
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-6 py-8">
        {/* Error Display */}
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg mb-6">
            <p className="font-medium">Error:</p>
            <p>{error}</p>
          </div>
        )}

        {/* Loading Indicator */}
        {isLoading && (
          <div className="bg-blue-50 border border-blue-200 text-blue-700 px-4 py-3 rounded-lg mb-6 flex items-center gap-3">
            <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>
            <span>Processing your request...</span>
          </div>
        )}

        {/* Tab Content */}
        {activeTab === 'analyze' && renderAnalysisTab()}
        {activeTab === 'voice' && renderVoiceTab()}
        {activeTab === 'memory' && renderMemoryTab()}
      </main>

      {/* Footer */}
      <footer className="bg-gray-800 text-white py-8 mt-12">
        <div className="max-w-7xl mx-auto px-6">
          <div className="grid md:grid-cols-3 gap-8">
            <div>
              <h3 className="font-bold text-lg mb-3 flex items-center gap-2">
                <span>🤖</span>
                Active Agents
              </h3>
              <div className="space-y-2 text-sm text-gray-300">
                <p>• UserProxy Agent</p>
                <p>• Voice Log Agent</p>
                <p>• Data Fetcher Agent</p>
                <p>• Time Analyzer Agent</p>
              </div>
            </div>
            <div>
              <h3 className="font-bold text-lg mb-3 flex items-center gap-2">
                <span>🔗</span>
                Data Sources
              </h3>
              <div className="space-y-2 text-sm text-gray-300">
